#!/usr/bin/bash

exitErr() {
    echo "$1" >&2
    echo "Usage: $0 file sector" >&2
    exit 1
}

if [ $# -lt 2 ]
then
    exitErr "Invalid number of arguments"
fi

FILE=$1

awk -v SECTOR="$2" -F "," 'BEGIN {
    num = 0;
    sum = 0;
    min = 2^32;
    max = 0;
};
{
    if($3 == SECTOR) {
        records[num] = $0;
        sum += $5;
        num++;
        if($5 < min)
            min = $5;
        if($5 > max)
            max = $5;
    }
};
END {
    if(num == 0)
        exit 1;

    out_info = "Shares from the sector \"" SECTOR "\"";
    out_delim = "-";

    printf("%s\n", out_info);
    for(i = 1; i < length(out_info); i++)
        out_delim = out_delim "-";

    print out_delim;

    for(i = 0; i < length(records); i++)
        print records[i];

    print out_delim;

    avg = sum / num;

    printf("minimum last price = %.2f\taverage last price = %.2f\tmaximum last price= %.2f\n", min, avg, max);
}' "$FILE"

RESULT=$?

